mapper.delete:
DELETE FROM article_view
WHERE user_id IN

    #{userId}

AND view_at < ALL (
    SELECT view_at
    FROM article_view
    WHERE user_id IN

        #{userId}

    ORDER BY view_at DESC
    LIMIT #{historyCount}
)

mapper.select:
SELECT id, title, digest, view_at viewAt, create_by
FROM article
         JOIN article_view
              ON article.id = #{articleId}
                  AND article_view.article_id = #{articleId}
                  AND article_view.user_id = #{userId}
                  AND article.is_delete = 0
ORDER BY view_at, id DESC
LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM article_ops
WHERE article_id = #{articleId}
  AND user_id = #{userId}
  AND mark != #{mark}

SELECT COUNT(*)
FROM article_ops
WHERE article_id = #{articleId}
  AND user_id = #{userId}
  AND favored!= #{favored}

SELECT mark, favored
FROM article_ops
WHERE article_id = #{articleId}
    AND user_id = #{userId}

SELECT id, title, content, digest, tag, update_at, is_delete
FROM article
WHERE update_at > #{updateAt}

SELECT article_id
FROM article_view
WHERE user_id = #{userId}
AND article_id NOT IN

    #{id}
